mapper.select:
/* selectFarm */ SELECT a.work_seq workSeq , a.serial_no serialNo , a.farm_no farmNo , a.connection_status , a.status
, a.input_count inputCount , a.water_qntty waterQntty , a.type type , a.reg_dtm regDtm , a.mod_dtm modDtm FROM tb_farm a WHERE a.serial_no = #{serialNo} AND a.farm_no = #{farmNo} AND a.type = #{type}
ORDER BY a.work_seq DESC LIMIT 1

/* selectFarm */ SELECT a.work_seq workSeq , a.serial_no serialNo , a.farm_no farmNo , (SELECT connection_status FROM tb_farm WHERE serial_no = a.serial_no AND connection_status IS NOT NULL AND reg_dtm >= DATE_ADD(NOW(), interval -1 hour) ORDER BY work_seq DESC LIMIT 1) connectionStatus , (SELECT status FROM tb_farm WHERE serial_no = a.serial_no AND status IS NOT NULL AND reg_dtm >= DATE_ADD(NOW(), interval -1 hour) ORDER BY work_seq DESC LIMIT 1) status , a.input_count inputCount , a.water_qntty waterQntty , a.type type , a.reg_dtm regDtm , a.mod_dtm modDtm FROM tb_farm a WHERE a.serial_no = #{serialNo}
AND a.farm_no = #{farmNo}
AND a.type = #{type} AND a.reg_dtm >= DATE_ADD(NOW(), interval -1 hour) ORDER BY a.work_seq DESC LIMIT 1

/* selectEggListByEgg */
SELECT IFNULL(b.work_seq, -1) work_seq , IFNULL(b.prod_ratio, 0) prod_ratio , IFNULL(b.qntty, 0) qntty , c.work_date FROM (SELECT work_seq , prod_ratio , qntty , DATE_FORMAT(reg_dtm, '%Y%m%d') work_date FROM tb_egg WHERE work_seq IN (SELECT MAX(work_seq) FROM (SELECT work_seq , DATE_FORMAT(reg_dtm, '%Y%m%d') work_date FROM tb_farm WHERE serial_no = #{serialNo} AND farm_no = #{farmNo} AND type = 'E' ANd reg_dtm >= STR_TO_DATE(CONCAT(#{fromDate}, '000000'), '%Y%m%d%H%i%s') AND reg_dtm < STR_TO_DATE(CONCAT(#{toDate}, '235959'), '%Y%m%d%H%i%s')) a GROUP BY a.work_date) AND farm_no = #{farmNo} AND grade = #{grade}) b RIGHT OUTER JOIN (SELECT w.work_date FROM
SELECT #{date} work_date
) c ON b.work_date = c.work_date ORDER BY c.work_date
