mapper.select:
/* selectEggListByEgg */

SELECT work_seq
     , prod_ratio
     , qntty
     , DATE_FORMAT(reg_dtm, '%Y%m%d') work_date
FROM   tb_egg
WHERE  work_seq IN (
                    SELECT MAX(work_seq)
                    FROM (
                        SELECT work_seq
                             , DATE_FORMAT(reg_dtm, '%Y%m%d') work_date
                        FROM   tb_farm
                        WHERE  serial_no = #{serialNo}
                        AND    farm_no = #{farmNo}
                        AND    type = 'E'
                        ANd    reg_dtm >= STR_TO_DATE(CONCAT(#{fromDate}, '000000'), '%Y%m%d%H%i%s')
                        AND    reg_dtm < STR_TO_DATE(CONCAT(#{toDate}, '235959'), '%Y%m%d%H%i%s')
                    ) a
                    GROUP BY a.work_date
       )
AND farm_no = #{farmNo}
AND grade = #{grade}
ORDER BY work_seq
