mapper.select:
SELECT user_id  userId
FROM   tb_user
WHERE  user_email = #{userEmail}

mapper.insert:
INSERT INTO tb_user
(
    user_id 
  , user_pw 
  , user_name
  , user_mphone
  , user_mphone1
  , user_mphone2
  , user_mphone3
  , user_email
  , user_address1
  , user_address2
  , serial_no
  , farm_name
  , photo_info
  , user_type
  , reg_dtm
  , mod_dtm
)
VALUES
(
    #{userId}
  , #{userPw}
  , #{userName}
  , #{userMphone}
  , #{userMphone1}
  , #{userMphone2}
  , #{userMphone3}
  , #{userEmail}
  , #{userAddress1}
  , #{userAddress2}
  , #{serialNo}
  , #{farmName}
  , #{photoInfo}
  , #{userType}
  , NOW()
  , NOW()
)

INSERT INTO tb_hen
(
    user_id 
  , farm_no 
  , hen_count 
  , ent_dt 
  , reg_dtm 
  , mod_dtm
)
VALUES
(
    #{userId}
  , #{farmNo}
  , #{henCount}
  , #{entDt}
  , NOW()
  , NOW()
)

mapper.update:
  UPDATE tb_user
  SET    user_name        = #{userName}
       
       , user_mphone      = #{userMphone}
       , user_mphone1     = #{userMphone1}
       , user_mphone2     = #{userMphone2}
       , user_mphone3     = #{userMphone3}
       , user_email       = #{userEmail}
       , user_address1    = #{userAddress1}
       , user_address2    = #{userAddress2}
       
       , farm_name        = #{farmName}
       , photo_info       = #{photoInfo}
       , user_type        = #{userType}
       
       , mod_dtm          = NOW()
WHERE user_id = #{userId}

UPDATE tb_user
SET    serial_no = #{serialNo}
     , confirm_dtm = NOW()
WHERE  user_id = #{userId}
